if (typeof jQuery !== "undefined") {
  jQuery(document).ready(function() {
    if (jQuery("#wipe-feed-cache").length) {
      jQuery("#wipe-feed-cache").on("click", function(evt) {
        jQuery("#wipe-message")
          .removeClass("show")
          .empty();
        evt.preventDefault();
        jQuery.ajax({
          type: "GET",
          url: "/wp-admin/admin-ajax.php",
          data: {action: "feed_delete_cache"},
          cache: false,
          success: function(data) {
            jQuery("#wipe-message")
              .addClass("show")
              .html("Successfully Wiped All Feeds");
          }
        });
      });
    }
    if (jQuery("#get-instagram").length) {
      jQuery("#get-instagram").on("click", function(evt) {
        jQuery("#get-instagram-message")
          .removeClass("show")
          .empty();
        evt.preventDefault();
        jQuery.ajax({
          type: "GET",
          url: "/wp-admin/admin-ajax.php",
          data: {action: "get_instagram"},
          cache: false,
          success: function(data) {
            jQuery("#get-instagram-message")
              .addClass("show")
              .html("Successfully Captured Instagram");
          }
        });
      });
    }
  });
}
